import { useState } from "react";
import "./App.css";

function App() {
      const [freeSpaces, setFreeSpaces] = useState({
            1: 1,
            2: 1,
            3: 1,
      });

      const [vehicleParked, setVehicleParked] = useState([]);

      const [vehicleQueue, setVehicleQueue] = useState([]);

      const [id, setId] = useState(0);

      const addVehicle = (e) => {
            while (e.parked < 4) {
                  if (freeSpaces[e.parked] > 0) {
                        return (
                              setFreeSpaces({
                                    ...freeSpaces,
                                    [e.parked]: freeSpaces[e.parked] - 1,
                              }),
                              setVehicleParked([...vehicleParked, e]),
                              setId(id + 1)
                        );
                  } else {
                        e.parked = e.parked + 1;
                        return addVehicle(e);
                  }
            }
            console.log('Sin espacio')
            e.parked = e.size;
            return setVehicleQueue([...vehicleQueue, e]), setId(id + 1);
      };

      const deleteVehicle = async (vehicle, ind) => {
            setFreeSpaces({
                  ...freeSpaces,
                  [vehicle.parked]: freeSpaces[vehicle.parked] + 1

            })
            await setVehicleParked(vehicleParked.filter((e, index) => {
                  return(
                        index !== ind
                  )
            }))

            .then(() => {
                  console.log(freeSpaces)
            })
      };


      return (
            <div className="App">
                  <section className="GUI-interface">
                        <div className="buttons">
                              <button
                                    onClick={(e) =>
                                          addVehicle({
                                                size: 1,
                                                parked: 1,
                                                name: "Motorcycle",
                                                id,
                                          })
                                    }
                              >
                                    Motorcycle
                              </button>
                              <button
                                    onClick={(e) =>
                                          addVehicle({
                                                size: 2,
                                                parked: 2,
                                                name: "Sedan",
                                                id,
                                          })
                                    }
                              >
                                    Sedan
                              </button>
                              <button
                                    onClick={(e) =>
                                          addVehicle({
                                                size: 3,
                                                parked: 3,
                                                name: "Truck",
                                                id,
                                          })
                                    }
                              >
                                    Truck
                              </button>
                        </div>
                        <div className="freeSpaces">
                              small: {freeSpaces[1]}
                              medium: {freeSpaces[2]}
                              large: {freeSpaces[3]}
                        </div>
                  </section>
                  <section className="vehicles-parked">
                        VEHICLES PARKED
                        {vehicleParked.map((vehicles, index) => {
                              return (
                                    <div>
                                          {vehicles.name}
                                          <button
                                                onClick={(e) =>
                                                      deleteVehicle(vehicles, index)
                                                }
                                          >
                                                {" "}
                                                DELETE{" "}
                                          </button>
                                    </div>
                              );
                        })}
                  </section>
                  <section className="parking-queue">
                        VEHICLES QUEUE
                        {vehicleQueue.map((vehicle) => {
                              return <div>{vehicle.name}</div>;
                        })}
                  </section>
            </div>
      );
}

export default App;
