import { useState } from "react";
import "./App.css";

function App() {
      const [freeSpaces, setFreeSpaces] = useState({
            1: 5,
            2: 5,
            3: 5,
      });

      const [vehicleParked, setVehicleParked] = useState([]);

      const [vehicleQueue, setVehicleQueue] = useState([]);

      const addVehicle = (e) => {
            while (e.size < 4) {
                  if (freeSpaces[e.size] > 0) {
                        e.parked = e.size
                        return (
                              setFreeSpaces({
                                    ...freeSpaces,
                                    [e.size]: freeSpaces[e.size] - 1,
                              }),
                              setVehicleParked([
                                    ...vehicleParked,
                                    e
                              ])
                        );
                  } else {
                        e.size = e.size + 1;
                        return addVehicle(e);
                  }
            }

            return setVehicleParked({
                  ...vehicleQueue,
                  e
            })

            console.log(`NO HAY MAS LUGAR PARA ${e.size}`);
      };

      return (
            <div className="App">
                  <section className="GUI-interface">
                        <div className="buttons">
                              <button
                                    onClick={(e) =>
                                          addVehicle({
                                                size: 1,
                                                parked: "",
                                                name:'Motorcycle'
                                          })
                                    }
                              >
                                    Motorcycle
                              </button>
                              <button
                                    onClick={(e) =>
                                          addVehicle({
                                                size: 2,
                                                parked: "",
                                                name:"Sedan"
                                          })
                                    }
                              >
                                    Sedan
                              </button>
                              <button
                                    onClick={(e) =>
                                          addVehicle({
                                                size: 3,
                                                parked: "",
                                                name:"Truck"
                                          })
                                    }
                              >
                                    Truck
                              </button>
                        </div>
                        <div className="freeSpaces">
                              small: {freeSpaces.small}
                              medium: {freeSpaces.medium}
                              large: {freeSpaces.large}
                        </div>
                  </section>
                  <section className="parking-queue">
                        {/* Queue: {parkingQueque} */}
                  </section>
            </div>
      );
}

export default App;
