import { useState } from "react";
import "./App.css";

function App() {
      const [freeSpaces, setFreeSpaces] = useState({
            1: 3,
            2: 3,
            3: 3,
      });

      const [vehicleParked, setVehicleParked] = useState([]);

      const [vehicleQueue, setVehicleQueue] = useState([]);

      const [id, setId] = useState(0);

      const addVehicle = (e) => {
            while (e.parked < 4) {
                  if (freeSpaces[e.parked] > 0) {
                        return (
                              setFreeSpaces({
                                    ...freeSpaces,
                                    [e.parked]: freeSpaces[e.parked] - 1,
                              }),
                              setVehicleParked([...vehicleParked, e]),
                              setId(id + 1)
                        );
                  } else {
                        e.parked = e.parked + 1;
                        return addVehicle(e);
                  }
            }

            e.parked = e.size;
            return setVehicleQueue([...vehicleQueue, e]), setId(id + 1);
      };

      return (
            <div className="App">
                  <section className="GUI-interface">
                        <div className="buttons">
                              <button
                                    onClick={(e) =>
                                          addVehicle({
                                                size: 1,
                                                parked: 1,
                                                name: "Motorcycle",
                                                id,
                                          })
                                    }
                              >
                                    Motorcycle
                              </button>
                              <button
                                    onClick={(e) =>
                                          addVehicle({
                                                size: 2,
                                                parked: 2,
                                                name: "Sedan",
                                                id,
                                          })
                                    }
                              >
                                    Sedan
                              </button>
                              <button
                                    onClick={(e) =>
                                          addVehicle({
                                                size: 3,
                                                parked: 3,
                                                name: "Truck",
                                                id,
                                          })
                                    }
                              >
                                    Truck
                              </button>
                        </div>
                        <div className="freeSpaces">
                              small: {freeSpaces.small}
                              medium: {freeSpaces.medium}
                              large: {freeSpaces.large}
                        </div>
                  </section>
                  <section className="parking-queue">
                        {/* Queue: {parkingQueque} */}
                  </section>
            </div>
      );
}

export default App;
