import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const ModalSizeParking = (props) => {
      const { modalShow, modalClose } = props;

      const [size, setSize] = useState({
            small:"",
            medium:"",
            large:""
      })

      const onChange = (e) => {
            setSize({
                  ...size,
                  [e.target.name]: e.target.value
            })
      }

      return (
            <Modal
                  show={modalShow}
                  onHide={() => modalClose()}
                  size="lg"
                  aria-labelledby="contained-modal-title-vcenter"
                  centered
            >
                  <Modal.Header closeButton>
                        <Modal.Title id="contained-modal-title-vcenter">
                              Choose the size of the parking
                        </Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                        <p>The default size is 5 spaces for each</p>
                        <Form.Group style={{ display: "flex" }}>
                              <Form.Group>
                                    <Form.Label>Small</Form.Label>
                                    <Form.Control
                                          type="number"
                                          placeholder="Small"
                                          onChange={e => onChange()}
                                    />
                              </Form.Group>
                              <Form.Group>
                                    <Form.Label>Medium</Form.Label>
                                    <Form.Control
                                          type="number"
                                          placeholder="Medium"
                                          onChange={e => onChange()}
                                    />
                              </Form.Group>
                              <Form.Group>
                                    <Form.Label>Large</Form.Label>
                                    <Form.Control
                                          type="number"
                                          placeholder="Large"
                                          onChange={e => onChange()}
                                    />
                              </Form.Group>
                        </Form.Group>
                  </Modal.Body>
                  <Modal.Footer>
                        <Button onClick={modalClose}>Close</Button>
                  </Modal.Footer>
            </Modal>
      );
};

export default ModalSizeParking;
